    public int longestValidParentheses(String s) {
        if (s == null || s.length() < 2) {
            return 0;
        }
        // 1. 自己解法，二维dp，复杂的表达式会严重超时，耗时82924ms
//        return doCalSelf(s);
        // 2. 暴力堆栈解法，大概63ms，优化后可以到15s，但是面对((((...((((会严重超时(提交力扣严重超时)
//        return doStack(s);
        // 3. 按照力扣的算法，采用一维动态规划，可以达到3ms，非常省时间
//        return doOneDiementionDp(s);
        // 4. 优化版的力扣代码：
//        System.out.println(useStatckLeetCode(")))))())"));
//        System.out.println(useStatckLeetCode("()()()()"));
        System.out.println(useStatckLeetCode("((()))"));
        return 0;
    }

    /**
     * 力扣推荐的：优化版的栈
     * @param s
     * @return
     */
    private int useStatckLeetCode(String s) {
        int maxans = 0;
        Stack<Integer> stack = new Stack<>();
        stack.push(-1);
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == '(') {
                stack.push(i);
            } else {
                stack.pop();
                if (stack.empty()) {
                    stack.push(i);
                } else {
                    maxans = Math.max(maxans, i - stack.peek());
                }
            }
        }
        return maxans;

    }

    /**
     * 一维dp，力扣推荐算法
     *
     * @param s
     * @return
     */
    private int doOneDiementionDp(String s) {
        int lenth = s.length();
        int dp[] = new int[lenth];
        if (s.charAt(0) == '(' && s.charAt(1) == ')') {
            dp[1] = 2;
        }
        int start = 2;
        while (start < lenth) {
            if (s.charAt(start) == '(') {
                start++;
                continue;
            }
            if (s.charAt(start) == ')') {
                if (s.charAt(start - 1) == '(') {
                    dp[start] = dp[start - 2] + 2;
                } else if (start - dp[start - 1] > 0 && s.charAt(start - dp[start - 1] - 1) == '(') {
                    dp[start] = dp[start - 1] + 2 +
                            (start - dp[start - 1] - 2 > 0 ? dp[start - dp[start - 1] - 2] : 0);
                }
            }
            start++;
        }
        int maxLen = 0;
        for (int a : dp) {
            maxLen = Math.max(a, maxLen);
        }
        return maxLen;

    }

    /**
     * 采用栈的方法
     *
     * @param s
     * @return
     */
    private int doStack(String s) {
        int lenth = s.length();
        Stack<Character> stack = new Stack<>();
        int index = 0;
        int maxLen = 0;
        while (index < lenth) {
            stack.clear();
            if (s.charAt(index) == ')') {
                index++;
                continue;
            }
            int start = index;
            int tmpMax = 0;
            while (start < lenth) {
                if (s.charAt(start) == ')') {
                    if (!stack.isEmpty()) {
                        stack.pop();
                    } else {
                        break;
                    }
                } else {
                    stack.push(s.charAt(start));
                }
                if (stack.isEmpty()) {
                    tmpMax = Math.max(tmpMax, start - index + 1);
                }
                start++;
            }
            // 加上下面这个判断后，可以节省四分之三的时间
            if (tmpMax > 0) {
                index = index + tmpMax;
            }
            index++;
            maxLen = Math.max(tmpMax, maxLen);

        }
        return maxLen;

    }

    /**
     * 这种自己的算法会超时
     *
     * @param s
     * @return
     */
    private int doCalSelf(String s) {
        int lenth = s.length();

        boolean dp[][] = new boolean[lenth + 1][lenth + 1];
        dp[0][0] = true;
        int maxLen = 0;
        for (int r = 2; r < lenth + 1; r++) {
            for (int l = r - 1; l > 0; l = l - 2) {
                // 如果两端凑成一对，中间必须为偶数个，否则凑不了
                if (r - l == 1 && s.charAt(r - 1) == ')' && s.charAt(l - 1) == '(') {
                    dp[l][r] = true;
                    if (dp[l][r] && r - l > maxLen) {
                        maxLen = r - l + 1;
                    }
                } else if (r - l >= 3 && s.charAt(r - 1) == ')' && s.charAt(l - 1) == '(') {
                    for (int i = l + 1; i < r; i++) {
                        dp[l][r] = dp[l][i] && dp[i + 1][r];
                        if (dp[l][r]) {
                            break;
                        }
                    }
                    dp[l][r] = dp[l][r] || dp[l + 1][r - 1];
                    if (dp[l][r] && r - l > maxLen) {
                        maxLen = r - l + 1;
                    }

                } else {
                    dp[l][r] = false;
                }
            }
        }
        return maxLen;

    }

